var now = new Date();
function createtime() {
  var t = new Date("10/01/2022 00:00:00");
  now.setTime(now.getTime() + 250);
  var e = (now - t) / 1e3 / 60 / 60 / 24,
    a = Math.floor(e),
    n = (now - t) / 1e3 / 60 / 60 - 24 * a,
    r = Math.floor(n);
  1 == String(r).length && (r = "0" + r);
  var s = (now - t) / 1e3 / 60 - 1440 * a - 60 * r,
    i = Math.floor(s);
  1 == String(i).length && (i = "0" + i);
  var o = (now - t) / 1e3 - 86400 * a - 3600 * r - 60 * i,
    l = Math.round(o);
  1 == String(l).length && (l = "0" + l);
  let g = "";
  (g =
    r < 18 && r >= 9
      ? `<img class='boardsign' src='https://img.shields.io/badge/%E5%93%B2%E5%AD%A6%E4%B8%BB%E4%B9%89%E9%9D%92%E5%B9%B4---%E6%91%B8%E9%B1%BC%E4%B8%AD-blue?style=social&logo=appveyor' title='我在干什么~'><span class='textTip'> <br> 本站居然运行了 ${a} 天</span><span id='runtime'> ${r} 小时 ${i} 分 ${l} 秒 </span> <i class='fas fa-heartbeat' style='color:red'></i>`
      : `<img class='boardsign' src='https://img.shields.io/badge/%E5%93%B2%E5%AD%A6%E4%B8%BB%E4%B9%89%E9%9D%92%E5%B9%B4---%E7%B4%AF%E4%BA%86-blue?style=social&logo=appveyor' title='天天摸鱼,天天开心~'><span class='textTip'> <br> 本站居然运行了 ${a} 天</span><span id='runtime'> ${r} 小时 ${i} 分 ${l} 秒 </span> <i class='fas fa-heartbeat' style='color:red'></i>`),
    document.getElementById("workboard") &&
      (document.getElementById("workboard").innerHTML = g);
}
setInterval(() => {
  createtime();
}, 250);
